                },
                "driver": {
                    "metadata": {
                        "label": "Driver",
                        "description": "",
                        "field_type": "hidden",
                        "encrypt": false,
                        "validation": {
                            "required": false
                        }
                    },
                    "value": "/opt/simba/spark/lib/64/libsparkodbc_sb64.so"
                },
                "organization": {
                    "metadata": {
                        "label": "Organization",
                        "description": "Optional",
                        "field_type": "text",
                        "encrypt": false,
                        "validation": {
                            "required": false
                        }
                    },
                    "value": ""
                },
                "user": {
                    "metadata": {
                        "label": "User",
                        "description": "Optional",
                        "field_type": "text",
                        "encrypt": false,
                        "validation": {
                            "required": false
                        }
                    },
                    "value": "<your-username>"
                }
            },
            "field_order": [
                "type",
                "method",
                "host",
                "port",
                "organization",
                "cluster",
                "endpoint",
                "user",
                "driver"
            ]
        }
    }
}

